mapper.select:
select e.* from employee e 


     	e.name like concat('%',#{emp.name},'%')



    limit #{page},#{size}

select count(*) from employee e



            and e.name like concat('%',#{emp.name},'%')




        and e.beginDate between #{beginDateScope[0]} and #{beginDateScope[1]}

mapper.insert:
insert into employee (id, name, gender, 
  birthday, idCard, wedlock, email, 
  phone, address,workID
  )
values (#{id,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{gender,jdbcType=CHAR}, 
  #{birthday,jdbcType=DATE}, #{idCard,jdbcType=CHAR}, #{wedlock,jdbcType=CHAR},  #{email,jdbcType=VARCHAR}, 
  #{phone,jdbcType=VARCHAR}, #{address,jdbcType=VARCHAR}, #{workID,jdbcType=CHAR}
  )
